import { useState } from 'react';
import { StudentClass } from './types/Student';


type StudentStateType={
    backup_student:StudentClass;
   
}

type StudentPropsType={
 
    student:StudentClass;
  }

export default function EditStudent({student}: StudentPropsType): React.ReactElement {
    const [Backupstudent, changeStudentData]=useState({
        name: student.Name, surname: student.Surname, index_nr: student.Surname, dataUrodzenia: student.dataUrodzenia, adres: '', grupa: '', stypendium: 0,
        marks: []
      });
      

      const changeValue=(e:React.FormEvent<HTMLInputElement>):void =>{
       console.log(student.Name)
      const Value = e.currentTarget.value;
      const name=e.currentTarget.name;
      switch(name) 
      {
        case 'name':student.Name=Value;break;
        case 'surname':student.Surname=Value;break;
      

      }

        
    }


    const BOP=():void=>{
    console.log("BLAM")
 }

    return (
    
        <div>
       
        Name in edit mode: <input type='text' name="name" value = {student.Name} onChange={(e)=>changeValue(e)}/>
        Surname in edit mode: <input type='text' name="surname" value={student.Surname} onChange={(e)=>changeValue(e)}/>
        Date of birth in edit mode: <input type='date' name="birthdate" onChange={(e)=>changeValue(e)}/>
        <button onClick={()=>BOP()}>Confirm edit</button>
      </div>
    
    
    )
}
