import { StudentClass } from './types/Student';


type StudentStateType={
    editing_student:StudentClass;
    backup_student:StudentClass;
   
}

type StudentPropsType={
 
    student:StudentClass;
  }

export default function EditStudent({student}: StudentPropsType): React.ReactElement {
    const [editing_student, changeStudentData]=useState({
        name: student.Name, surname: student.Surname, index_nr: student.Index_nr, dataUrodzenia: student.dataUrodzenia, adres: student.adres, grupa: student.grupa, stypendium: student.stypendium,
        marks: student.marks
      });
      const [backup_student, RestoreStudentData]=useState({
        name: student.Name, surname: student.Surname, index_nr: student.Index_nr, dataUrodzenia: student.dataUrodzenia, adres: student.adres, grupa: student.grupa, stypendium: student.stypendium,
        marks: student.marks
      });
    
      
      

      const changeValue=(e:React.FormEvent<HTMLInputElement>):void =>{
        const Value = e.currentTarget.value;
        const name=e.currentTarget.name;
     switch(name) 
      {
        case 'name':
            console.log("Before: " + editing_student.name + " After: " + Value);
            editing_student.name=Value;break;
        case 'surname':
            console.log("Before: " + editing_student.surname + "After: " + Value);
            editing_student.surname=Value;break;
        case 'index':
             let numberifiedValue: number = +Value
        console.log("Before: " + editing_student.index_nr + "After: " + Value);
            editing_student.index_nr=numberifiedValue;
            break;
      }  
      changeStudentData(editing_student)
    }


    const BOP=():void=>{
    console.log("BLAM")
 }

    const Rewind=():void=>{
     
    }

    return (
    
        <div>
       
        Name in edit mode: <input type='text' name="name" value = {editing_student.name} onChange={(e)=>changeValue(e)}/>
        Surname in edit mode: <input type='text' name="surname" value={editing_student.surname} onChange={(e)=>changeValue(e)}/>
        Index in edit mode: <input type='number' name="index" value={editing_student.index_nr} onChange={(e)=>changeValue(e)}/>
        Date of birth in edit mode: <input type='date' name="birthdate" onChange={(e)=>changeValue(e)}/>
        <button onClick={()=>BOP()}>Confirm edit</button>
        <button onClick={()=>Rewind()}>Cancel edit</button>
      </div>
    
    
    )
}
